// Array multipleChoices is generated by generate_multipleChoices function in previous code
// This code is to put the answer into DOM
var AddChoice_to_DOM =  function(){
	$("#answer1").html(multipleChoices[0]);
	$("#answer2").html(multipleChoices[1]);
	$("#answer3").html(multipleChoices[2]);
	$("#answer4").html(multipleChoices[3]);
}

// This code will change the <i> class once the answer is selected, and reset the other
// <i> to "fa fa-circle -o fa-1.5x"

$("#answer").on("click", "p", function(){
	$("i").className = "fa fa-circle -o fa-1.5x"
	$(this).children("i").className = "fa fa-dot-circle-o fa-1.5x"
})

